import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import Particle from "../Particle";
import Github from "./Github";
import Techstack from "./Techstack";
import Aboutcard from "./AboutCard";
import akhilImg from "../../Assets/akhilpfp.png";
import Toolstack from "./Toolstack";

function About() {
  return (
    <Container fluid className="about-section">
      <Particle />
      <Container>
        <Row style={{ justifyContent: "center", padding: "10px" }}>
          <Col
            md={7}
            style={{
              justifyContent: "center",
              paddingTop: "30px",
              paddingBottom: "50px",
            }}
          >
            <h1 style={{ fontSize: "2.1em", paddingBottom: "20px" }}>
              Know Who I Am
            </h1>
            <Aboutcard />
          </Col>
          <Col
            md={5}
            style={{ paddingTop: "5px", paddingBottom: "50px" }}
            className="about-img"
          >
<Tilt
  tiltMaxAngleX={15} // Adjust the X-axis tilt
  tiltMaxAngleY={15} // Adjust the Y-axis tilt
  scale={1.05}       // Scale on hover
  transitionSpeed={300} // Smooth transition
>
  <img
    src={akhilImg}
    alt="about"
    className="img-fluid"
    style={{
      maxWidth: "90%", // Reduce the size of the image
      margin: "0 auto", // Center the image horizontally
      display: "block", // Ensure it's centered
    }}
  />
</Tilt>
          </Col>
        </Row>
        <h1 className="project-heading">
          Professional <strong className="purple">Skillset </strong>
        </h1>

        <Techstack />

        <h1 className="project-heading">
          <strong className="purple">Tools</strong> I use
        </h1>
        <Toolstack />

        {/* <Github /> */}
      </Container>
    </Container>
  );
}

export default About;
